  padding: 20px;
  font-size: 20px;
  color: ${props => props.색상};
`;

function About() {
  useEffect(() => {
    let timer = setTimeout(() => {
      alertFn(false);
    }, 2000);
    return () => {
      console.log('about페이지에서 벗어났습니다.');
    };
  });
  let [alert, alertFn] = useState(true);
  return (
    <div className='container'>
      <H4 색상='blue'>About 페이지</H4>
      {alert === true ? (
