import logo from './logo.svg';
import React, { useState } from 'react';
import { Navbar, Container, Nav, NavDropdown } from 'react-bootstrap';
import prds from './data.js';
import './App.css';
import { Link, Route, Switch } from 'react-router-dom';

function App() {
  let [prd, fnPrd] = useState(prds);
  return (
    <div className='App'>
      <Navbar bg='light' expand='lg'>
        <Container>
          <Navbar.Brand href='#home'>React-Bootstrap</Navbar.Brand>
          <Navbar.Toggle aria-controls='basic-navbar-nav' />
          <Navbar.Collapse id='basic-navbar-nav'>
            <Nav className='me-auto'>
              <Nav.Link href='#home'>Home</Nav.Link>
              <Nav.Link href='#link'>Link</Nav.Link>
              <NavDropdown title='Dropdown' id='basic-nav-dropdown'>
                <NavDropdown.Item href='#action/3.1'>Action</NavDropdown.Item>
                <NavDropdown.Item href='#action/3.2'>
                  Another action
                </NavDropdown.Item>
                <NavDropdown.Item href='#action/3.3'>
                  Something
                </NavDropdown.Item>
                <NavDropdown.Divider />
                <NavDropdown.Item href='#action/3.4'>
                  Separated link
                </NavDropdown.Item>
              </NavDropdown>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      {/* 메인페이지 */}
      <Route exact path='/'>

        <div className='jumbotron visual'>
          <h1 className='display-4'>
            Mind Bridge. <br />
            2021 FALL
          </h1>
          <p className='lead'>
            This is a simple hero unit, a simple jumbotron-style component for
            calling extra attention to featured content or information.
          </p>
          <hr className='my-4' />
          <p>
            It uses utility classNamees for typography and spacing to space
            content out within the larger container.
          </p>
          <a className='btn btn-light btn-lg' href='#' role='button'>
            Learn more
          </a>
        </div>
        <div className='container products'>
          <div className='row'>
            {prd.map((v, i) => {
              return <Product prd={v} i={i} key={i}></Product>;
            })}
          </div>
        </div>
      </Route>
      <Route path='/detail'>
        <div>디테일 페이지</div>
      </Route>
      {/* <Route path='/test' component={Product}></Route> */}
    </div>
  );
}

function Product(props) {
  return (
    <div className='col-md-4'>
      <img
        src={`https://eunsuneun.github.io/react-shop/src/img/prd0${
          props.i + 1
        }.jpg`}
      />
      <h4>{props.prd.title}</h4>
      <p>{props.prd.content}</p>
      <p className='price'>{props.prd.price}won</p>
    </div>
  );
}

export default App;
